/* Basic Reset & Body Styles */
body {
    margin: 0; /* Remove default body margin */
    padding: 0; /* Remove default body padding */
    font-family: Arial, sans-serif; /* Basic font */
    display: flex; /* Use flexbox for layout */
    flex-direction: column; /* Stack toolbar and canvas vertically */
    min-height: 100vh; /* Make body take full viewport height */
    background-color: #f8f8f8; /* Light background for the page */
    padding: 20px;
}

img {
    background-color: white;
}

.completed-drawing {
    width: 500px;
    height: 500px;
    overflow: hidden;
    border-radius: 0 0 20px 20px;
}

.app-container {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 20px;
}

.user-profile {
    display: flex;
    width: 500px;
    padding: 10px;
    justify-content: start;
    font-size: 14px;
    color: #666;
}

/* Toolbar Styles */
.toolbar {
    display: flex; /* Arrange items horizontally */
    flex-wrap: wrap; /* Allow items to wrap on smaller screens */
    align-items: center; /* Vertically align items in the center */
    gap: 10px; /* Space between toolbar items */
    flex-shrink: 0;
    justify-content: space-between;
}

/* Toolbar Item Styles */
.toolbar button {
    padding: 8px 15px;
    font-size: 14px;
    border: 1px solid #b0b0b0;
    border-radius: 4px;
    background-color: #ffffff;
    cursor: pointer;
    transition: background-color 0.2s ease, border-color 0.2s ease; /* Smooth transitions */
}

.toolbar button:hover {
    background-color: #f0f0f0;
    border-color: #909090;
}

/* Style for the 'active' tool button (add/remove this class with JS) */
.toolbar button.active {
    background-color: #d0e0ff; /* Light blue background */
    border-color: #a0c0ff; /* Slightly darker blue border */
    font-weight: bold;
}

/* Style for the color picker */
.toolbar .color-picker {
    width: 40px; /* Fixed width */
    height: 30px; /* Fixed height */
    padding: 1px; /* Minimal padding around the color area */
    border: 1px solid #b0b0b0;
    border-radius: 4px;
    background-color: #ffffff; /* Background for the input itself */
    cursor: pointer;
    vertical-align: middle; /* Align better with buttons */
}

.toolbar-left {
    display: flex;
    flex-direction: row;
    gap: 10px;
}

.toolbar-right {
    display: flex;
    flex-direction: row;
    gap: 10px;
}

.color-picker.selected {
    border: 3px solid #000; /* Change border color when selected */
    border-color: #000; /* Change border color when selected */
}

.color-picker.selected:hover {
    border: 3px solid #000;
}

.card {
    background-color: white;
    position: relative;
    border: 1px solid lightgray;
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.1);
    border-radius: 20px;
    /* overflow: hidden; */
    z-index: 1;
}

.card-header {
    display: flex;
    flex-direction: column;
    gap: 5px;
    padding: 15px 15px 10px 15px;
    font-size: 20px;
    border-bottom: 1px solid lightgray;
}

.card-header-content {
    display: flex;
    flex-direction: row;
    gap: 10px;
    justify-content: space-between;
    align-items: center;
}

.profile-picture {
    background-color: lightgray;
    width: 40px;
    height: 40px;
    border-radius: 100%;
    border: 1px solid lightgray;
    font-size: 14px;
    display: flex;
    justify-content: center;
    align-items: center;
}

.prompt {
    display: flex;
    flex-direction: column;
    gap: 5px;
    font-size: 20px;
}

.prompt-date {
    display: flex;
    justify-content: start;
    font-size: 14px;
    color: #666;
}

/* Style for the range slider (line width) */
.toolbar input[type="range"] {
    cursor: pointer;
    vertical-align: middle; /* Align better with buttons */
    width: 120px; /* Adjust width as needed */
}

/* Optional: Add a label if you use one for the slider */
.toolbar label {
    margin-left: 5px; /* Space before the label */
    margin-right: 3px; /* Space after the label */
    font-size: 14px;
    vertical-align: middle; /* Align better with controls */
}

#drawingCanvasContainer {
    display: none;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}

#promptList {
    display: none;
    flex-direction: column;
    align-items: center;
    gap: 20px;
    margin-bottom: 50px;
}

/* Canvas Styles */
#drawingCanvas {
    background-color: #fff;
    display: block;
    position: relative;
    z-index: 1;
    width: 900px;
    height: 900px;
    border-radius: 0 0 20px 20px;
}

